mapper.insert:
INSERT INTO zk_dubbo_service_node (
    service_id, interface_name, version, address, last_sync_time, gmt_created, gmt_modified
) VALUES (
    #{serviceId}, #{interfaceName}, #{version}, #{address}, #{lastSyncTime}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT 
FROM zk_dubbo_service_node
WHERE service_id = #{serviceId} AND address = #{address}
