INSERT INTO zk_provider_method (
    provider_id, method_name, method_order, gmt_created, gmt_modified
) VALUES (
    #{providerId}, #{methodName}, #{methodOrder}, #{createdAt}, #{updatedAt}
)

INSERT INTO zk_provider_method (
    provider_id, method_name, method_order, gmt_created, gmt_modified
) VALUES

    (
        #{item.providerId}, #{item.methodName}, #{item.methodOrder}, #{item.createdAt}, #{item.updatedAt}
    )

ON DUPLICATE KEY UPDATE
    method_order = VALUES(method_order),
    gmt_modified = VALUES(gmt_modified)
