mapper.insert:
INSERT INTO zk_provider_parameter (
    provider_id, param_key, param_value, param_order, gmt_created, gmt_modified
) VALUES (
    #{providerId}, #{paramKey}, #{paramValue}, #{paramOrder}, #{createdAt}, #{updatedAt}
)

INSERT INTO zk_provider_parameter (
    provider_id, param_key, param_value, param_order, gmt_created, gmt_modified
) VALUES

    (
        #{item.providerId}, #{item.paramKey}, #{item.paramValue}, #{item.paramOrder}, #{item.createdAt}, #{item.updatedAt}
    )

ON DUPLICATE KEY UPDATE
    param_value = VALUES(param_value),
    param_order = VALUES(param_order),
    gmt_modified = VALUES(gmt_modified)
